import '../style/Auth.css'
import React from 'react';
import axios from "axios";
// import {render} from "@testing-library/react";

const Users = () => {
    // const getUsers = async () => {
    //     const users = axios.get('http://localhost:3000/api/users');
    //
    //     return users;
    // }
    //
    // const users = getUsers();

    function Blog(props) {
        const sidebar = (
            <ul>
                {props.posts.map((post) =>
                    <li key={post.id}>
                        {post.title}
                    </li>
                )}
            </ul>
        );
        const content = props.posts.map((post) =>
            <div key={post.id}>
                <p>{post.content}</p>
            </div>
        );
        return (
            <div>
                {sidebar}
                <hr />
                {content}
            </div>
        );
    }

    const posts = [
        {id: 1, title: 'Привіт, світе', content: 'Ласкаво просимо до вивчення React!'},
        {id: 2, title: 'Установка', content: 'React можна встановити через npm.'}
    ];


    return (
        <div className="users">
            <div className="centered">
                <p>Users</p>
                <div>
                    <Blog posts={posts} />
                    {/*<button>{users}</button>*/}
                    {/*{users.map(person => <button>{person.fullName}</button>)}*/}
                </div>
            </div>
        </div>
    );
}

export default Users;
