import '../style/Auth.css'
import React, {useState} from 'react';
import history from './history';
import { BrowserRouter as Router, Route } from "react-router-dom";
import PassReset from "./PassReset";

const SignIn = ({loginUser}) => {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');

    const onSubmit = (e) => {
        e.preventDefault();

        if (!login) {
            alert('please enter login');
            return;
        }

        loginUser({login, password});

        setLogin('');
        setPassword('')
    }

    const email = async (user) => {
        const res = await fetch('http://localhost:3000/api/auth/password-reset', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(user),
        });

        const data = await res.json();

        console.log(data);
    }

    return (
        <Router>
            <div className="centered">
                <p>Sign in</p>
                <form className="signIn">
                    <p>login</p>
                    <input type="text" name="login" placeholder="enter your login" value={login} onChange={(e) =>
                        setLogin(e.target.value)}/>
                    <p>password</p>
                    <input type="password" name="password" placeholder="enter your password" value={password} onChange={(e) =>
                        setPassword(e.target.value)}/>
                    <p></p>
                    <input type="submit" name="send" value="Send" onClick={onSubmit}/>
                    <input type="button" name="reset" value="Forgot password?" onClick={() => history.replace('api/auth/password-reset')}></input>
                </form>
            </div>
            <Route path="/api/auth/password-reset">
                <div className="container">
                    <PassReset resetPass={email}/>
                </div>
            </Route>
        </Router>

    );
}

export default SignIn;
