import '../App.css'
import React from 'react';
import {useState} from 'react';

const SignIn = () => {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');

    const onSubmit = (e) => {
        e.preventDefault();

        if (!login) {
            alert('please enter login');
            return;
        }

       loginUser({login, password});

        setLogin('');
        setPassword('')
    }

    const loginUser = async (user) => {
        console.log(user);
        const res = await fetch('http://localhost:3000/api/auth/login', {
            method: 'POST',
            body: JSON.stringify(user),
        })

        const data = await res.json();

        console.log(data);
    }


    return (
        <div className="sign in">
            <ul>
                <li><a className="active" href="#home">Home</a></li>
                <li><a href="#news">News</a></li>
                <li><a href="#contact">Contact</a></li>
                <li><a href="#about">About</a></li>
            </ul>
            <div className="centered">
                <p>Sign in</p>
                <form className="signIn">
                    <p>login</p>
                    <input type="text" name="login" placeholder="enter your login" value={login} onChange={(e) =>
                    setLogin(e.target.value)}/>
                    <p>password</p>
                    <input type="password" name="password" placeholder="enter your password" value={password} onChange={(e) =>
                    setPassword(e.target.value)}/>
                    <p></p>
                    <input type="button" name="send" value="Send" onClick={onSubmit}/>
                </form>
            </div>
        </div>
    );
}

export default SignIn;
