import React from "react";
import { BrowserRouter as Router, Route, useHistory} from "react-router-dom";
import axios from 'axios';

import SignIn from "./components/SignIn";
import SignUp from "./components/SignUp";
import Users from "./components/Users";
import PassReset from "./components/PassReset";


const App = () => {
    const history = useHistory();

    const login = async (user) => {
        const res = await fetch('http://localhost:3000/api/auth/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(user),
        });

        const data = await res.json();

        console.log(data);
    }

    const register = async (user) => {
        const res = await fetch('http://localhost:3000/api/auth/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(user),
        });

        const data = await res.json();

        console.log(data);
    }

    const email = async (user) => {
        const res = await fetch('http://localhost:3000/api/auth/password-reset', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(user),
        });

        const data = await res.json();

        console.log(data);
    }

    const getUsers = async () => {
        const users = axios.get('http://localhost:3000/api/users');

        return users;
    }

  return (
      <div>
          <button className="navBar" onClick={() => history.push('/')}>Home</button>
          <button className="navBar" /*onClick={() => history.push('/api/auth/users')}*/>Users</button>
          <button className="navBar" /*onClick={() => history.push('/api/auth/login')}*/>Sign In</button>
          <button className="navBar" onClick={() => history.push('/api/auth/register')}>Sign Up</button>
          <Router>
              <Route path="/api/auth/login">
                  <div className="container">
                      <SignIn loginUser={login}/>
                  </div>
              </Route>
              <Route path="/api/auth/register">
                  <div className="container">
                      <SignUp registerUser={register}/>
                  </div>
              </Route>
              <Route path="/api/auth/users">
                  <div className="container">
                      <Users getUsers={getUsers}/>
                  </div>
              </Route>
              <Route path="/api/auth/password-reset">
                  <div className="container">
                      <PassReset resetPass={email}/>
                  </div>
              </Route>
          </Router>
      </div>

  );
}

export default App;
