import '../style/Auth.css'
import React, {useState} from 'react';
import history from 'history';

const PassReset = ({resetPass}) => {
    const [email, setEmail] = useState('');

    const onSubmit = (e) => {
        e.preventDefault();

        if (!email) {
            alert('please enter email');
            return;
        }

        resetPass({email});

        setEmail('');
    }

    const onReset = (e) => {
        e.preventDefault();
    }

    return (
        <div className="centered">
            <p>Password Reset</p>
            <form className="signIn">
                <p>email</p>
                <input type="text" name="email" placeholder="enter your email" value={email} onChange={(e) =>
                    setEmail(e.target.value)}/>
                <p></p>
                <input type="submit" name="send" value="Send" onClick={onSubmit}/>
                <input type="button" name="reset" value="Sign in" onClick={() => history.replace('/auth/login')}></input>
            </form>
        </div>
    );
}

export default PassReset;
